mapper.insert:
insert into refund_address (
    refund_address_pk, product_pk, refund_address_name, refund_address_post_number,
    refund_representative_address, refund_detail_address, refund_phone_number, refund_extra_phone_number
)
values(
          #{refund_address_pk}, #{product_pk}, #{refund_address_name}, #{refund_address_post_number},
          #{refund_representative_address}, #{refund_detail_address}, #{refund_phone_number}, #{refund_extra_phone_number}
      );
